DECLARE
	@varContenedor VARCHAR(5)
	,@varMedallaBronce VARCHAR(15)
	,@varBitacoraADF VARCHAR(50)
	,@varFechaProceso DATE
	,@varDiasBorrarCargar INT
	,@varFechaInicio DATE

SELECT TOP 1 @varContenedor = Valor FROM dbo.Parametros WHERE Clave = 'ContenedorLagoDatosReg' ORDER BY Codigo DESC;
SELECT TOP 1 @varMedallaBronce = Valor FROM dbo.Parametros WHERE Clave = 'MedallaBronce' ORDER BY Codigo DESC;
SELECT TOP 1 @varDiasBorrarCargar = (CAST(Valor AS INT) * -1) FROM dbo.Parametros WHERE Clave = 'DiasCargarBorrar' ORDER BY Codigo DESC;
SELECT TOP 1 @varFechaProceso = CAST(Valor AS DATE) FROM dbo.Parametros WHERE Clave = 'FechaProceso' ORDER BY Codigo DESC;
SELECT TOP 1 @varBitacoraADF = Valor FROM dbo.Parametros WHERE Clave = 'BitacoraCoherenciaDataFactory' ORDER BY Codigo DESC;

SET @varFechaInicio = DATEADD(DAY, @varDiasBorrarCargar, @varFechaProceso)

DECLARE
	@varTablaSalida TABLE (Contenedor VARCHAR(5), Medalla VARCHAR(15), FechaProceso VARCHAR(10), BitacoraADF VARCHAR(50));

WHILE @varFechaInicio <= @varFechaProceso 
BEGIN

	INSERT INTO @varTablaSalida 
	SELECT @varContenedor, @varMedallaBronce, CAST(@varFechaInicio AS VARCHAR(10)), @varBitacoraADF

	SET @varFechaInicio = DATEADD(DAY, 1, @varFechaInicio)

END 

SELECT TOP 1 Contenedor, Medalla, FechaProceso, BitacoraADF FROM @varTablaSalida;
